var sessionHelper = function (){
	var self = this;

	self.verifySession = function(req){

		if(req.session.session_data){
      		return req.session.session_data;
		}else{
      		return false;
   		}
	};
	self.deleteSession = function(req){
		req.session.destroy();
	}
};

module.exports = sessionHelper;
